(1105, 164, '_components_0_overlay_box', 'field_593ef5881bfc8_field_5946a41fd01c3'),
(1106, 164, 'components_3_header', 'Our Mission'),
(1107, 164, '_components_3_header', 'field_59402e4788936_field_59402e0ca9677'),
(1108, 164, 'components_3_paragraph', 'It is not only what is seen, but what is felt that makes Preserve products special. Every piece of product we create is designed to match our multi-faceted mission: Sustain planet EARTH, preserve the ART form of refined hand-made paper, and evoke feelings of LOVE and delight. From our sustainable, eco-friendly sourcing to the care we press literally by hand into our papers products, all combined with the platform we give our artisans to rise above rural poverty, you will feel our passion and see the endless possibilities of PRESERVE.'),
(1109, 164, '_components_3_paragraph', 'field_59402e4788936_field_59402e1ca9678'),
(1110, 164, 'components_3_image', '162'),
(1111, 164, '_components_3_image', 'field_59402e4788936_field_59402e29a9679'),
(1112, 78, 'components_0_header', 'TEES NOT TREES™'),
(1113, 78, '_components_0_header', 'field_593ef5881bfc8_field_593ef52a1dbdc'),
(1114, 78, 'components_0_description', 'Made from recycled tee shirt scraps discarded by the large-scale garment manufacturing industry in India, Preserve Handmade Paper is the most sustainable paper that exists to date. A 100% tree-free product, our paper is made from cotton scraps and dried in the sun naturally—creating a pleasing tactile sensation and a one-of-a-kind piece.'),
