(5446, 350, 'components_0_header', 'Story of Preserve'),
(5447, 350, '_components_0_header', 'field_593ef5881bfc8_field_593ef52a1dbdc'),
(5448, 350, 'components_0_description', 'Preserve Brands is committed to preserving the ancient art form of hand papermaking—a centuries old, eco-friendly process, resulting in beautifully crafted paper, made by artisans living and working in rural villages in India and in need of socioeconomic support. This ancient tradition is ready to be rediscovered by conscientious retailers and consumers who believe that artistry and sustainability go hand-in-hand.'),
(5449, 350, '_components_0_description', 'field_593ef5881bfc8_field_593ef52e1dbdd'),
(5450, 350, 'components_0_background_image', '345'),
