(8834, 496, 'components_0_header', 'TEES NOT TREES'),
(8835, 496, '_components_0_header', 'field_593ef5881bfc8_field_593ef52a1dbdc'),
(8836, 496, 'components_0_description', 'Made from recycled tee shirt scraps discarded by the large-scale garment manufacturing industry in India, Preserve Handmade Paper is the most sustainable paper that exists to date. A 100% tree-free product, our paper is made from cotton scraps and dried in the sun naturallycreating a pleasing tactile sensation and a one-of-a-kind piece.'),
(8837, 496, '_components_0_description', 'field_593ef5881bfc8_field_593ef52e1dbdd'),
(8838, 496, 'components_0_overlay_box', '1'),
